

#int
#float
#str   # karakterlerden oluşan yapı.ad soyad gibi bilgi
#bool  # doğru yanlış var yok gibi mantıksal veri türüdür



age = 28                        #int 
weight = 55.6                   #float
name = "Sinem"                  #str
isStudent = False               #bool 



print(type(age))
print(type(weight))
print(type(name))
print(type(isStudent))


# int to float

result = float(age)
print(result)
 
# float to int 

result = int(weight)
print(result)

# bool to str

result = str(isStudent)
print(result)
print(type(result))

#bool to int 

result = int(isStudent)
print(result)
print(type(result))

